SELECT `uc`.`id` AS `uc_id`, `uc`.`code` AS `uc_code`, `uc`.`status` AS `uc_status`, `uc`.`remark` AS `uc_remark`, `uc`.`create_at` AS `uc_create_at`, `uc`.`update_at` AS `uc_update_at`, `uc`.`delete_at` AS `uc_delete_at`, `uc`.`user_id` AS `uc_user_id`, `ucu`.`id` AS `ucu_id`, `ucu`.`pid` AS `ucu_pid`, `ucu`.`group_id` AS `ucu_group_id`, `ucu`.`vip_level` AS `ucu_vip_level`, `ucu`.`role` AS `ucu_role`, `ucu`.`username` AS `ucu_username`, `ucu`.`password` AS `ucu_password`, `ucu`.`salt` AS `ucu_salt`, `ucu`.`mobile` AS `ucu_mobile`, `ucu`.`score` AS `ucu_score`, `ucu`.`status` AS `ucu_status`, `ucu`.`create_at` AS `ucu_create_at`, `ucu`.`update_at` AS `ucu_update_at`, `ucu`.`delete_at` AS `ucu_delete_at`, `ucu`.`profileId` AS `ucu_profileId`, `contributions`.* FROM `user_creator` `uc` LEFT JOIN `user` `ucu` ON `ucu`.`id`=`uc`.`user_id`  LEFT JOIN (SELECT `u`.`id` AS `u_id`, `u`.`pid` AS `u_pid`, `u`.`group_id` AS `u_group_id`, `u`.`vip_level` AS `u_vip_level`, `u`.`role` AS `u_role`, `u`.`username` AS `u_username`, `u`.`password` AS `u_password`, `u`.`salt` AS `u_salt`, `u`.`mobile` AS `u_mobile`, `u`.`score` AS `u_score`, `u`.`status` AS `u_status`, `u`.`create_at` AS `u_create_at`, `u`.`update_at` AS `u_update_at`, `u`.`delete_at` AS `u_delete_at`, `u`.`profileId` AS `u_profileId` FROM `user_contribution` `contributions` LEFT JOIN `user` `u` ON `contributions`.`user_id`=`u`.`id` LIMIT 4) `contributions` ON contributions.user_id=`ucu`.`id` WHERE `uc`.`delete_at` IS NULL